mapper.select:
select * from orders


        user_id=#{userId}


        and status=#{status}


        and number=#{number}


        and phone=#{phone}


        and order_time > #{beginTime}


        and order_time < #{endTime}


order by order_time desc

select sum(amount) from orders


        order_time > #{begin}


        and order_time < #{end}


        and status=#{status}

select  orders.*,address_book.location,address_book.adcode
from orders left join address_book on address_book.id=orders.address_book_id
where orders.id=#{orderId}

select * from orders


        user_id=#{ordersPageQueryDTO.userId}


        and status=#{ordersPageQueryDTO.status}


        and number=#{ordersPageQueryDTO.number}


        and phone=#{ordersPageQueryDTO.phone}


        and order_time > #{ordersPageQueryDTO.beginTime}


        and order_time < #{ordersPageQueryDTO.endTime}

    and merchant_id = #{merchantId}

order by order_time desc
